import React, { Component } from 'react';

class EventPractice extends Component {
  state = {
    message: ''
  };

  render() {
    return (
      <div>
        <h1>EventPractice</h1>
        <input
          type="text"
          name="message"
          placeholder="아무거나 입력해 보세요"
          value={this.state.message}
          onChange={e => {
            this.setState({
              message: e.target.value
            });
          }}
        />
        <button
          onClick={() => {
            alert(this.state.message);
            this.setState({
              message: ''
            });
          }}
        />
      </div>
    );
  }
}

export default EventPractice;
